// OS-create-password
function password (text: string, num: number) {
    if (!(text == "password" || (text == "w" || (text == "pp" || text == "wp")))) {
        blockSettings.writeNumber(text, num)
        return true
    } else {
        return false
    }
}
function drag () {
    if (mySprite2 && mySprite5) {
        if (mySprite.overlapsWith(mySprite2)) {
            mySprite2.follow(mySprite)
            disalbleclick = true
        }
        if (mySprite.overlapsWith(mySprite5)) {
            mySprite5.follow(mySprite)
            disalbleclick = true
        }
    }
}
